SELECT * FROM cuenta c INNER JOIN empresa e ON c.id_empresa = e.id_empresa


SELECT * FROM bloques b INNER JOIN bloques_noticias bn ON b.id = bn.bloque_id INNER JOIN empresa e ON b.empresa_id = e.id_empresa

SELECT e.id_empresa as empresaid, e.nombre as empresa, t.id_tema as temaid, t.nombre as tema, c.id_cuenta as cuentaid, CONCAT(c.nombre, ' ', c.apellidos) AS nombre_cuenta, c.email as correo FROM empresa_tema_cuenta etc INNER JOIN empresa e ON etc.id_empresa = e.id_empresa INNER JOIN tema t ON etc.id_tema = t.id_tema INNER JOIN cuenta c ON etc.id_cuenta = c.id_cuenta WHERE e.id_empresa = 21;

